import React, {useState} from "react";

import { postGuest } from "../services/GuestsServices";

const Form = ({addGuest}) => {

    const [formData, setFormData] = useState({});

    const handleSubmit = (e) => {
        console.log(e)
        e.preventDefault();
        formData["name"] = e.target.name.value;
        formData["emailAddress"] = e.target.emailAddress.value;
        formData["isCheckedIn"] = false;
        postGuest(formData).then((data) => {
            addGuest(data);
        });
    }

    // const onChange = (e) => {
    //     formData[e.target.id] = e.target.value;
    //     setFormData(formData);
    // }


    return (
        // <div>

        //     <form className="" onSubmit={handleSubmit} method="post">
        //         <label htmlFor="name">Name:</label>
        //         <input onChange={onChange} type="text" id="name" required />

        //         <label htmlFor="emailAddress">Email Address:</label>
        //         <input onChange={onChange} type="email" id="emailAddress" required />

        //         <input type="submit" value="Save" id="save" />

        //     </form>
        // </div>
        <div>

            <form className="" onSubmit={handleSubmit} method="post">
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" required />

                <label htmlFor="emailAddress">Email Address:</label>
                <input type="email" id="emailAddress" required />

                <input type="submit" value="Save" id="save" />

            </form>
        </div>
    )
}


export default Form;
