import React, {useState} from "react";

const Form = () => {

    const [formData, setFormData] = useState({});

    const handleSubmit = (e) => {
        e.preventDefault();
        formData["name"] = formData.name;
        formData["emailAddress"] = formData.emailAddress;
        formData["isCheckedIn"] = false;
        postGame(formData).then((data) => {
            addGuest(data);
        });
    }

    const onChange = (e) => {
        formData[e.target.id] = e.target.value;
        setFormData(formData);
    }


    return (
        <div>

            <form className="" onSubmit={handleSubmit} method="post">
                <label htmlFor="name">Name:</label>
                <input onChange={onChange} type="text" id="name" required />

                <label htmlFor="emailAddress">Email Address:</label>
                <input onChange={onChange} type="email" id="emailAddress" required />

                <input type="submit" value="Save" id="save" />

            </form>
        </div>
    )
}


export default Form;
